import React, { useEffect, useState } from 'react'
import { Tabs } from 'antd'
import axios from 'axios';
import Loader from '../components/Loader';
import Error from '../components/Error';

const { TabPane } = Tabs;
function Profilescreen() {

    const user = JSON.parse(localStorage.getItem('currentUser')).data

    useEffect(() => {
        console.log("heelloo")
        if (!user) {
            window.location.href = '/login'
        }

    }, [])

    return (
        <div className='ml-3 mt-3'>
            <Tabs defaultActiveKey='1'>
                <TabPane tab='Profile' key='1'>
                    <p>My profile</p>
                    <br />
                    <p>Name: {user.name}</p>
                    <p>Email: {user.email}</p>
                    <p>isAdmin: {user.isAdmin ? "YES" : "NO"}</p>
                </TabPane>
                <TabPane tab='Bookings' key='2'>
                    <MyBookings />
                </TabPane>
            </Tabs>
        </div>
    )
}

export default Profilescreen

export function MyBookings() {
    const user = JSON.parse(localStorage.getItem('currentUser')).data
    const [bookings, setBookings] = useState([])
    const [loading, setloading] = useState(false);
    const [error, seterror] = useState();
    useEffect(() => {
        const fetchBookings = async () => {
            try {
                setloading(true)
                const rooms = await (await axios.post("http://localhost:5000/api/bookings/getbookingsbyuserid", { userid: user._id })).data
                console.log(rooms)
                setBookings(rooms)
                setloading(false)
            } catch (error) {
                console.log(error)
                setloading(false)
                seterror(error)
            }
        }
        fetchBookings();
    }, [])

    return (
        <div>
            <div className='row'>
                <div className='col-md-6'>
                    {loading && <Loader />}
                    {bookings && (bookings.map((booking) => {
                        return (<div className='bs'>
                            <h1>{booking.room}</h1>
                            <p><b></b>: {booking._id}</p>
                            <p>Transaction Id: {booking.transactionId}</p>
                            <p>Checkin date: {booking.fromDate}</p>
                            <p>Checkout date: {booking.toDate}</p>
                            <p>Amount: {booking.totalAmount}</p>
                            <p>Status: {booking.status==='booked'?"CONFIRMED":"CANCELLED"}</p>
                        </div>
                        )
                    }))}
                </div>
            </div>
        </div>
    )

}
