import React, {useState, useEffect} from 'react';
import axios from "axios";
import { useParams } from 'react-router-dom';
import Loader from '../components/Loader';
import Error from '../components/Error';
import moment from 'moment'

function Bookingscreen() {
    let { roomid,fromDate,toDate } = useParams(); // Destructure the roomid from the useParams object
    // console.log(roomid); // roomid should now be a string or undefined

    const [room, setroom] = useState();
    const [loading, setloading] = useState(true);
    const [error, seterror] = useState();

    const totalDays = moment.duration(moment(toDate,'DD-MM-YYYY').diff(moment(fromDate,'DD-MM-YYYY'))).asDays()+1
    const [totalAmount,setTotalAmount] = useState()
    useEffect(() => {
        async function fetchData() {
            try {
                setloading(true);
                const data = (await axios.post("http://localhost:5000/api/rooms/getroombyid", {roomid: roomid})).data;
                setroom(data);
                setTotalAmount(totalDays*data.rentPerDay)
                setloading(false);
            } catch (error) {
                seterror(true);
                console.log(error);
                setloading(false);
            }
        }
        fetchData();
      }, []); 
    

      const wrapperFunction = () => {
        //do something
        bookRoom();
        checkoutHandler(totalAmount);
    }
    
    const checkoutHandler = async (amount) => {

        try {
            const {data} = await axios.post("http://localhost:5000/api/payment/checkout", {
                amount
            });
            console.log(data);

            const options = {
                key: "rzp_test_p4HuA905NOORxZ", // Enter the Key ID generated from the Dashboard
                amount: "50000", // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
                currency: "INR",
                name: "Acme Corp", //your business name
                description: "Test Transaction",
                image: "https://example.com/your_logo",
                order_id: "order_9A33XWu170gUtm", //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
                handler: function (response){
                    alert(response.razorpay_payment_id);
                    alert(response.razorpay_order_id);
                    alert(response.razorpay_signature)
                },
                "prefill": { //We recommend using the prefill parameter to auto-fill customer's contact information, especially their phone number
                    "name": "Gaurav Kumar", //your customer's name
                    "email": "gaurav.kumar@example.com", 
                    "contact": "9000090000"  //Provide the customer's phone number for better conversion rates 
                },
                "notes": {
                    "address": "Razorpay Corporate Office"
                },
                "theme": {
                    "color": "#3399cc"
                }
            };
            var rzp1 = new Razorpay(options);
            rzp1.on('payment.failed', function (response){
                    alert(response.error.code);
                    alert(response.error.description);
                    alert(response.error.source);
                    alert(response.error.step);
                    alert(response.error.reason);
                    alert(response.error.metadata.order_id);
                    alert(response.error.metadata.payment_id);
            });
            document.getElementById('rzp-button1').onclick = function(e){
                rzp1.open();
                e.preventDefault();
            }

        } catch (error) {
            console.log(error);
        }
    };

    async function bookRoom() {
        const bookingDetails = {
            room,
            userid: JSON.parse(localStorage.getItem('currentUser')).data._id,
            fromDate,
            toDate,
            totalAmount,
            totalDays
        }

        try {
            const result = await axios.post("http://localhost:5000/api/bookings/bookroom", bookingDetails)
        } catch (error) {
            
        }
    }  
      
    return (
        <div style={{margin: '50px'}}>
            {loading ? (<Loader/>) : room ? (<div>
                <div className='row justify-content-center mt-5 bs'>
                    <div className='col-md-6'>
                        <h1>{room.name}</h1>
                        <img src={room.imageUrls[0]} className='bigimg'/>
                    </div>
                    <div className='col-md-6'>
                        <div style={{textAlign: 'right'}}>
                            <h1>Booking Details</h1>
                            <hr/>
                            <b>
                                <p>Name : {JSON.parse(localStorage.getItem('currentUser')).data.name}</p>
                                <p>From Date : {fromDate}</p>
                                <p>To Date : {toDate}</p>
                                <p>Max Count : {room.maxCount}</p>
                            </b>
                        </div>
                        <div style={{textAlign: 'right'}}>
                            <h1>Amount</h1>
                            <hr/>
                            <b>
                                <p>Total days : {totalDays}</p>
                                <p>Rent per day : {room.rentPerDay}</p>
                                <p>Total Amount : {totalAmount}</p>
                            </b>
                        </div>
                        <div style={{float: 'right'}}>
                            <button className='btn btn-primary' onClick={wrapperFunction}>Pay Now</button>
                        </div>
                    </div>
                </div>
            </div>
            ): (<Error/>)}
        </div>
    );
}

export default Bookingscreen;
