import React, {useState, useEffect} from "react";
import axios from "axios";
import Room from "../components/Room";
import Loader from '../components/Loader';
import Error from '../components/Error';
import { DatePicker, Space } from 'antd';
import 'antd/dist/reset.css'
import moment from 'moment'

const { RangePicker } = DatePicker;

function Homescreen(){

    const [fromDate,setFromDate] = useState();
    const [toDate,setToDate] = useState();
    const [rooms, setrooms] = useState([]);
    const [duplicateRooms,setduplicateRooms] = useState([])
    const [loading, setloading] = useState();
    const [error, seterror] = useState();
    useEffect(() => {
        async function fetchData() {
            try {
                setloading(true);
                const data = (await axios.get("http://localhost:5000/api/rooms/getAllRooms")).data;
                setrooms(data);
                setduplicateRooms(data);
                setloading(false);
            } catch (error) {
                seterror(true);
                console.log(error);
                setloading(false);
            }
        }
        fetchData();
      }, []); 

    function filterByDate(dates) {
        console.log(dates[0].format('DD-MM-YYYY'))
        console.log(dates[1].format('DD-MM-YYYY'))
        setFromDate(dates[0].format('DD-MM-YYYY'))
        setToDate(dates[1].format('DD-MM-YYYY'))
        var temprooms = []
        var availability = false
        for(const room of duplicateRooms)
        {
            if(room.currentBookings.length>0)
            {
                for(const booking of room.currentBookings)
                {
                    const fromDateMoment = moment(booking.fromDate, 'DD-MM-YYYY');
                    const toDateMoment = moment(booking.toDate, 'DD-MM-YYYY');
                    const startDateMoment = moment(dates[0].format('DD-MM-YYYY'), 'DD-MM-YYYY');
                    const endDateMoment = moment(dates[1].format('DD-MM-YYYY'), 'DD-MM-YYYY');
                    if (startDateMoment.isBetween(fromDateMoment, toDateMoment) || endDateMoment.isBetween(fromDateMoment, toDateMoment)) {
                        // If start date or end date is between booking dates, room is not available
                        availability = false;
                        break; // No need to check further bookings for this room
                    }
                    if (fromDateMoment.isBetween(startDateMoment, endDateMoment) || toDateMoment.isBetween(startDateMoment, endDateMoment)) {
                        // If booking dates overlap with selected range, room is not available
                        availability = false;
                        break; // No need to check further bookings for this room
                    }
                }      
            }
            if(availability==true || room.currentBookings.length==0)
                temprooms.push(room);  
        }
    }
      
    return(
        <div className="container">
            
            <div className="row mt-5">
                <div className="col-md-3">
                    <RangePicker format='DD-MM-YYYY' onChange={filterByDate}/>
                </div>
            </div>

            <div className="row justify-content-center mt-5">
                {loading ? (<Loader/>) : rooms.length>=1 ? (rooms.map(room=>{
                        return <div className="col-md-9 mt-2">
                            <Room room={room} fromDate={fromDate} toDate={toDate} />
                        </div>
                })) : (<Error/>)}
            </div>
        </div>
    )
    
};

export default Homescreen;
