import React, {useState, useEffect} from 'react';
import axios from "axios";
import { useParams } from 'react-router-dom';

function Bookingscreen() {
    let { roomid } = useParams(); // Destructure the roomid from the useParams object
    // console.log(roomid); // roomid should now be a string or undefined

    const [rooms, setroom] = useState();
    const [loading, setloading] = useState(true);
    const [error, seterror] = useState();

    useEffect(() => {
        async function fetchData() {
            try {
                setloading(true);
                const data = (await axios.post("http://localhost:5000/api/rooms/getroombyid", {roomid: roomid})).data;
                setroom(data);
                setloading(false);
            } catch (error) {
                seterror(true);
                console.log(error);
                setloading(false);
            }
        }
        fetchData();
      }, []); 

    return (
        <div>
            {loading ? (<h1>Loading...</h1>) : error ? (<h1>Error</h1>) : (<div>
                <div className='row'>
                    <div className='col-md-5'>
                        <h1>{room.name}</h1>
                        <img src={room.imageUrls}/>
                    </div>
                </div>
            </div>
            )}
        </div>
    );
}

export default Bookingscreen;
